import React from 'react';
import LetterButton from './components/LetterButton';
import { letras as data } from './mock/letras';

const App = () => {
  const letras = data.map((l) => l.letra); // ✅ Extraer solo las letras
  const [completedLetters, setCompletedLetters] = React.useState([]);

  const handleLetterClick = (letra) => {
    if (!completedLetters.includes(letra)) {
      setCompletedLetters([...completedLetters, letra]);
    }
  };

  return (
    <div className="flex flex-wrap justify-center">
      {letras.map((letra) => (
        <LetterButton
          key={letra}
          letra={letra}
          onClick={handleLetterClick}
          completed={completedLetters.includes(letra)}
        />
      ))}
    </div>
  );
};

export default App;
