import React, { useEffect } from 'react';

const PayPalHostedButton = () => {
  useEffect(() => {
    // Verifica si ya est치 cargado
    if (window.paypal && window.paypal.HostedButtons) {
      renderButton();
    } else {
      const script = document.createElement('script');
      script.src = 'https://www.paypal.com/sdk/js?client-id=BAA08Aliiaqe29PxD86s85Fso0_5ydIfde83nw-41JN8PBqcHUzlU9p_JtMPbwroys1oxd-Ku6qkHj9jpo&components=hosted-buttons&disable-funding=venmo&currency=USD';
      script.async = true;
      script.onload = () => {
        if (window.paypal?.HostedButtons) {
          renderButton();
        } else {
          console.error('PayPal HostedButtons no est치 disponible.');
        }
      };
      document.body.appendChild(script);
    }

    function renderButton() {
      window.paypal.HostedButtons({
        hostedButtonId: 'SPLRLRZKJYKHC',
      }).render('#paypal-container-SPLRLRZKJYKHC');
    }
  }, []);

  return (
    <div
      id="paypal-container-SPLRLRZKJYKHC"
      className="fixed bottom-4 right-4 z-50"
    />
  );
};

export default PayPalHostedButton;
