import React from 'react';

const DonateButton = () => {
  const handleDonate = () => {
    // Replace with your actual PayPal or payment link
    window.open('YOUR_PAYPAL_OR_PAYMENT_LINK', '_blank');
  };

  return (
    <button
      onClick={handleDonate}
      className="fixed bottom-4 right-4 bg-yellow-500 text-gray-800 px-6 py-3 rounded-full shadow-lg hover:bg-yellow-600 transition-colors text-lg font-semibold"
    >
      Apoya el Desarrollo ❤️
    </button>
  );
};

export default DonateButton;
