import React, { useEffect, useRef } from 'react';

const PayPalButton = () => {
  const paypalRef = useRef();

  useEffect(() => {
    const script = document.createElement("script");
    script.src = "https://www.paypal.com/sdk/js?client-id=BAA08Aliiaqe29PxD86s85Fso0_5ydIfde83nw-41JN8PBqcHUzlU9p_JtMPbwroys1oxd-Ku6qkHj9jpo&components=buttons&currency=USD";
    script.addEventListener("load", () => {
      window.paypal.Buttons({
        style: {
          layout: "vertical",
          color: "gold",
          shape: "rect",
          label: "donate"
        },
        createOrder: function(data, actions) {
          return actions.order.create({
            purchase_units: [{
              amount: {
                value: '2.00' // cambia este valor si deseas
              }
            }]
          });
        },
        onApprove: function(data, actions) {
          return actions.order.capture().then(function(details) {
            alert('Gracias por tu donación, ' + details.payer.name.given_name + '!');
          });
        }
      }).render(paypalRef.current);
    });
    document.body.appendChild(script);
  }, []);

  return (
    <div className="fixed bottom-4 right-4 z-50">
      <div ref={paypalRef}></div>
    </div>
  );
};

export default PayPalButton;
