import React from 'react';
import { FaPaypal } from 'react-icons/fa'; // Asegúrate de tener react-icons instalado

const DonateButton = () => {
  const handleDonate = () => {
    window.open('https://www.paypal.com/donate?hosted_button_id=TU_ID_AQUI', '_blank');
  };

  return (
    <button
      onClick={handleDonate}
      className="fixed bottom-4 right-4 z-50 bg-yellow-400 hover:bg-yellow-500 text-gray-900 px-6 py-3 rounded-full shadow-xl flex items-center gap-2 text-lg font-bold transition duration-300"
    >
      <FaPaypal className="text-blue-600 text-2xl" />
      Apoya el Desarrollo
    </button>
  );
};

export default DonateButton;
