import React, { useEffect, useRef } from 'react';

const PayPalButton = () => {
  const paypalRef = useRef();

  useEffect(() => {
    // Evitar cargar el script más de una vez
    if (window.paypal) {
      renderButton();
    } else {
      const script = document.createElement("script");
      script.src = "https://www.paypal.com/sdk/js?client-id=BAA08Aliiaqe29PxD86s85Fso0_5ydIfde83nw-41JN8PBqcHUzlU9p_JtMPbwroys1oxd-Ku6qkHj9jpo&components=buttons&currency=USD";
      script.async = true;
      script.onload = () => {
        if (window.paypal) {
          renderButton();
        } else {
          console.error("PayPal SDK no se cargó correctamente.");
        }
      };
      script.onerror = () => {
        console.error("Error al cargar el script de PayPal.");
      };
      document.body.appendChild(script);
    }

    function renderButton() {
      window.paypal.Buttons({
        style: {
          layout: "vertical",
          color: "gold",
          shape: "rect",
          label: "donate"
        },
        createOrder: (data, actions) => {
          return actions.order.create({
            purchase_units: [{
              amount: {
                value: '2.00'
              }
            }]
          });
        },
        onApprove: (data, actions) => {
          return actions.order.capture().then((details) => {
            alert(`Gracias por tu donación, ${details.payer.name.given_name}!`);
          });
        }
      }).render(paypalRef.current);
    }
  }, []);

  return (
    <div className="fixed bottom-4 right-4 z-50">
      <div ref={paypalRef}></div>
    </div>
  );
};

export default PayPalButton;
